                  {elm.member_users[0].first_name.match(/\b(\w)/g)}
                </LogoBtn>
                <NameDiv>
                  <p>
                    <span style={{ fontSize: "20px" }}>
                      {elm.member_users[0].first_name}
                    </span>
                    <br />
                    {elm.last_message.message}
                  </p>
                </NameDiv>
              </UserBox>
            </>
          );
        })}
        <HistoryBox>gfdgfdg</HistoryBox>
      </Container>
    </div>
  );
};
export default ChatUi;
